<?phprequire_once(FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php');
class mainAppView extends viewSuperClass_Core{	public function display_Main_Application_Layout()	{		//css		$content = $this->renderTemplate('Project/1_Website/Design/Applications/Commerce/Main_App_Layout/templates/css_links.phtml');		response::getInstance()->addContentToStack('local_css',array('commerce css'=>$content));		//javascript		$content = $this->renderTemplate('Project/1_Website/Design/Applications/Commerce/Main_App_Layout/templates/js_links.phtml');		response::getInstance()->addContentToStack('global_javascript_bottom',array('commerce js'=>$content));				$content = $this->renderTemplate('Project/1_Website/Design/Applications/Commerce/Main_App_Layout/templates/main_app_layout.phtml');		response::getInstance()->addContentToTree(array('MAIN_CONTENT'=>$content));	}}?>