<?phprequire_once(FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php');/** * The Main Layout gets all the content for the areas outside the main focus. * Eg. if you are on www.MyResort.com/accommodation or even www.MyResort.com,  * the focus will be on a page * but you have other tasks like header photo, logo, navigation * these will be needed on most pages.  *  */
class mainView extends viewSuperClass_Core{		public function getMainLayout() 	{		//HTML5 SHIV ======================================================================================================		$content = $this->renderTemplate('Project/'.DOMAIN.'/Design/Main_Layout/templates/html5Shiv.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('HTML5 SHIV'=>$content),'TOP');		//Myk placed this here to make older browsers which do not support HTML5 function properly						//let's put JQuery and AJAX here because there may be some sites that don't use one... just in case				//JQUERY LIBRARY ==========================================================================================================		$content = $this->renderTemplate(FILE_ACCESS_CORE_DESIGN.'/Plugins/JQuery/templates/jquery_link.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('JQUERY LIBRARY'=>$content),'TOP');				//JQUERY AJAX ======================================================================================================			$content = $this->renderTemplate(FILE_ACCESS_CORE_DESIGN.'/Plugins/Ajax/templates/jquery_link.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('JQUERYAJAX'=>$content));	       	//GLOBAL SCRIPTS / CSS / PLUGIN ======================================================================		$content = $this->renderTemplate('Project/'.DOMAIN.'/Design/Main_Layout/templates/global_js_links.phtml');		response::getInstance()->addContentToStack('global_javascript_bottom',array('GLOBALSCRIPTS'=>$content));				$content = $this->renderTemplate('Project/'.DOMAIN.'/Design/Main_Layout/templates/global_css_links.phtml');		response::getInstance()->addContentToStack('global_css',array('MAIN LAYOUT CSS'=>$content));				$content = $this->google_analytics;
		response::getInstance()->addContentToTree(array('GOOGLE_ANALYTICS'=>$content));				$content = $this->renderTemplate('Project/'.DOMAIN.'/Design/Main_Layout/templates/main.phtml');		response::getInstance()->addContentToTree(array('TOPLEVEL'=>$content));				//DISPLAY LEFT NAV FOR PRODUCTS ON ALL PAGES		$this->displayApplicationBlock('Products', 'productsNavigation');   }        //=================================================================================================        	public function setGoogleAnalytics($analytics) { $this->google_analytics = $analytics; }   	}?>