<?phprequire_once(FILE_ACCESS_CORE_CODE.'/Framework/MVC_superClasses_Core/viewSuperClass_Core/viewSuperClass_Core.php');
class mainAppView extends viewSuperClass_Core{		public function display_Main_Application_Layout()	{		$content = $this->renderTemplate('Project/2_Enterprise/Design/Applications/_Common/templates/css_links.phtml');		response::getInstance()->addContentToStack('global_css',array('COMMON CS'=>$content));				$content = $this->renderTemplate('Project/2_Enterprise/Design/Applications/_Common/templates/js_links.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('COMMON JS'=>$content));				$content = $this->renderTemplate('Project/2_Enterprise/Design/Applications/User_Account_Admin/Main_App_Layout/templates/css_links.phtml');		response::getInstance()->addContentToStack('global_css',array('ACCOUNT CS'=>$content));				$content = $this->renderTemplate('Project/2_Enterprise/Design/Applications/User_Account_Admin/Main_App_Layout/templates/js_links.phtml');		response::getInstance()->addContentToStack('global_javascript_top',array('ACCOUNT JS'=>$content));				$content = $this->renderTemplate('Project/'.DOMAIN.'/Design/Applications/User_Account_Admin/Main_App_Layout/templates/main_app_layout.phtml');		response::getInstance()->addContentToTree(array('MAIN_CONTENT'=>$content));			}	}?>